INSERT INTO `t_project` VALUES (5,'专利2项，论文8篇','针对全景稀疏动态光场采集系统中表面不规则场景的获取问题，研究光场的非均匀采集方法，通过分析场景表示维度间的稀疏冗余特性，构建场景表面属性、摄像机部署、重建质量之间的关系模型，提出重建场景高保真复原的评价机制，在保证重建质量最优的前提下，最大化降低采集数据冗余度。','2018-12-31','2015-01-01',1,'表面不规则场景的光场非均匀采集方法研究（国家自然基金面上项目）','纵向项目',NULL,5),(7,'','3DHEVC视频快速算法的研究；目标：1、发表论文3篇；2、申请专利2项；3、申请软件著作权3项；4、系统检测报告1份；5、工作总结，技术报告；6、验收','2016-12-30','2012-06-01',0,'三维视频编码及传输关键技术研究和系统示范（创新团队子项目）','实验室项目',NULL,5),(11,'','硕士毕业课题，完成车辆定位算法，移植WAVE协议，搭建过路口辅助驾驶系统','2016-12-01','2016-03-01',0,'基于车辆定位的过路口辅助驾驶系统及其关键技术研究','实验室项目',NULL,5),(12,'','搭建LNG远程数据采集系统','2016-12-31','2016-03-10',0,'LNG项目','横向项目',NULL,5),(13,'','通过网络接收MIDI协议曲目，主板解析MIDI协议的歌曲，从板根据命令演奏钢琴','2016-12-30','2016-03-01',0,'智能钢琴','横向项目',NULL,5),(20,'','i-Key，新型TCU，Otocol项目扫尾等','2016-12-30','2016-03-03',0,'台湾项目','横向项目',NULL,5),(23,NULL,'创建人机物联微信公众号，管理项目，任务考核，以及文化建设','2016-06-30','2016-03-10',1,'“人机物联”微信公众号','实验室项目',NULL,5),(25,'','设计一种新型家居智能网关存储平台。致力于解决，物联网环境下不同硬件接口、数据协议的智能设备接入问题，设备引起的大数据存储、查询问题、异构数据协同问题、信息安全、设备安全问题。主要研究和工作内容如下：(1)设备硬件接口、数据协议研究分析；(2)基于嵌入式 Linux的智能网关开发；(3)分布式云平台设计开发；(4)智能网关安全保障系统、智能控制系统设计与开发；（5）WEB、手机 APP 等用户接口开发','2017-04-30','2016-02-23',0,'智能网关存储平台设计与应用(省新苗项目)','实验室项目',NULL,5),(30,'','','2019-03-11','2015-03-11',0,'lp实时调度','纵向项目',NULL,7),(33,'','','2016-12-31','2016-01-01',0,'fm财务管理系统','横向项目',NULL,7),(35,'','','2016-03-30','2016-03-11',1,'工业大数据（两化融合基金项目）','纵向项目',NULL,16),(37,'国际论文：5；含合著论文：1\r\n国内专刊：4；含国外专刊：1\r\n软著：2 原理样机：1\r\n人才培养 博士：2；硕士：15\r\n交流 来访：2；人次：2\r\n        出访：2；人次：2\r\n举办会议：国内：2；人次：5','完成基于统计模型的低剂量三维x光机','2017-04-30','2016-03-11',0,'低剂量X射线三维成像系统（国际合作项目）','纵向项目',NULL,16),(40,'','开展非结构化的透镜阵列真三维视频采集技术，实破透镜/微透镜阵列光场重绘的低空间和深度分辨率局限，实现从非结构化运动光场恢复结构的真三维模型重建。','2017-12-31','2016-03-11',0,'基于光场的真三维视频采集与生成','纵向项目',NULL,22),(61,'验收报告，PPT，答辩会要','验收报告，PPT，答辩会要','2016-05-30','2016-04-08',0,'创新团队子项目验收','实验室项目',NULL,5),(102,'小论文1篇，大论文1篇','','2016-12-31','2016-09-26',0,'立体匹配算法研究','实验室项目',NULL,5),(104,NULL,NULL,'2020-10-15','2017-09-15',1,'2017新生','实验室项目',5,5),(106,NULL,NULL,'2022-12-31','2018-07-01',1